import React, { useState, useEffect } from "react";
import Modal from "../Modal/Modal";
import styles from "./ViewFactory.module.css";
import useHttp from "./../../hooks/use-http";
import { addToFavs, getDataInFavs } from "../../lib/api";

const ViewFactory = (props) => {
  const { sendRequest, status, error } = useHttp(addToFavs);
  const {
    sendRequest: getKeys,
    status: getStatus,
    error: getError,
    data: loadedFavs,
  } = useHttp(getDataInFavs, true);
  useEffect(() => {
    getKeys();
  }, [getKeys]);
  const compareIds = Object.keys(loadedFavs);
  console.log(compareIds);

  const [isAddedToFavs, setIsAddedToFavs] = useState(false);
  const HandleCloseModal = () => {
    props.onCloseModal();
  };
  const addToFavsHandler = () => {
    console.log(props.dataViewFactory);
    sendRequest({
      savedData: props.dataViewFactory,
      id: props.dataViewFactory.DOTCode,
    });
    setIsAddedToFavs(true);
  };
  return (
    <Modal>
      {" "}
      <div className={styles.modalWindow}>
        {!isAddedToFavs && (
          <button onClick={addToFavsHandler}>Add to favs</button>
        )}
        {isAddedToFavs && <div>Already Added</div>}
        <div className={styles.factoryInfo}>
          <div>{props.dataViewFactory.Name}</div>
          <div>{props.dataViewFactory.Country}</div>
          <div>{props.dataViewFactory.City}</div>
          <div>{props.dataViewFactory.Address}</div>
          <div>{props.dataViewFactory.PostalCode}</div>
          <div>{props.dataViewFactory.DOTCode}</div>
          <div>{props.dataViewFactory.Status}</div>
        </div>
        <button onClick={HandleCloseModal}>Close</button>
      </div>
    </Modal>
  );
};
export default ViewFactory;
