import React, { useEffect, useState } from "react";
import { getDataInFavs } from "../lib/api";
import useHttp from "./../hooks/use-http";
import FavouriteList from "./../components/Favourite/FavouriteList";

const Favourite = () => {
  const [refreshFavsPage, setRefreshFavsPage] = useState(false);
  const refreshFavsHandler = () => {
    setRefreshFavsPage(true);
  };
  const {
    sendRequest,
    status,
    error,
    data: loadedFavs,
  } = useHttp(getDataInFavs, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return <div>pending</div>;
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if ((status === "completed" && !loadedFavs) || loadedFavs.length === 0) {
    return <div className="centered focused">No favs found!</div>;
  }

  const firstIteration = Object.entries(loadedFavs);
  let secondItem = [];
  firstIteration.forEach((element) => {
    const test = Object.entries(element[1]);
    secondItem.push(test[0][1]);
  });

  // console.log(firstIteration, secondItem);
  // console.log(firstIteration, secondItem[0][1]);

  return (
    <>
      <FavouriteList
        favs={secondItem}
        onRefreshFavsHandler={refreshFavsHandler}
      />
    </>
  );
};
export default Favourite;
