import React, { useCallback, useEffect, useState } from "react";
import { fetchCar } from "../lib/api";
import useHttp from "./../hooks/use-http";

const Home = () => {
  const { sendRequest, status, data: loadedData } = useHttp(fetchCar, true);
  const [vehicles, setVehicles] = useState("");

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);
  console.log(status, data);

  if (status === "pending") {
    return <div className="centered">Please wait...</div>;
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if (status === "completed" && !loadedData) {
    return <div className="centered focused">There is no Info yet!</div>;
  }

  let content = <p>No cars</p>;
  if (status === "completed" && loadedData) {
    content = vehicles.map((data) => {
      return (
        <>
          <div className="focused">
            <span>{data.MakeId}</span>
            <span>{data.MakeName}</span>
          </div>
        </>
      );
    });
  }

  return (
    <>
      <div>Home</div>
      <div>{content}</div>
    </>
  );
};

export default Home;
