import React, { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { fetchCarForDatabase } from "../lib/api";
import useHttp from "./../hooks/use-http";

import styles from "./Database.module.css";

const Database = () => {
  const [getCar, setGetCar] = useState("");
  const [getYear, setGetYear] = useState("");
  const [isCarSearch, setIsCarSearch] = useState(true);
  const { sendRequest, status, data, error } = useHttp(fetchCarForDatabase);
  const getCarHandler = (e) => {
    e.preventDefault();
    setGetCar(e.target.value);
  };
  const getYearHandler = (e) => {
    e.preventDefault();
    setGetYear(e.target.value);
  };
  const submitCarHandler = (e) => {
    e.preventDefault();
    console.log(getCar, getYear);
    sendRequest({ manufacturer: getCar, year: getYear });
  };
  //   useEffect(() => {}, [getCarHandler]);
  console.log(status, data);

  const searchtypeHandler = () => {
    setIsCarSearch(() => !isCarSearch);
  };

  return (
    <>
      {status}
      <div className={styles.checkbox}>
        <label className={styles.form - control}>
          <input type="radio" name="radio" checked />
          Car Search
        </label>

        <label className={styles.form - control}>
          <input type="radio" name="radio" />
          Factory Search
        </label>
      </div>
      <div>
        <button onClick={searchtypeHandler}>
          {isCarSearch ? "Search auto" : "Search factory"}
        </button>
      </div>
      <form onSubmit={submitCarHandler}>
        <label htmlFor="cars">Choose a car:</label>
        <select onChange={getCarHandler} id="cars" name="cars">
          <option value="volvo">Volvo</option>
          <option value="saab">Saab</option>
          <option value="nissan">Nissan</option>
          <option value="audi">Audi</option>
        </select>
        <select onChange={getYearHandler} id="years" name="years">
          <option value="2012">2012</option>
          <option value="2013">2013</option>
          <option value="2014">2014</option>
          <option value="2015">2015</option>
        </select>
        <button>Get a car</button>
      </form>
    </>
  );
};
export default Database;
