import React, { useEffect } from "react";
import { getDataInFavs } from "../lib/api";
import useHttp from "./../hooks/use-http";
import FavouriteList from "./../components/Favourite/FavouriteList";

const Favourite = () => {
  const {
    sendRequest,
    status,
    error,
    data: loadedFavs,
  } = useHttp(getDataInFavs, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return <div>pending</div>;
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if ((status === "completed" && !loadedFavs) || loadedFavs.length === 0) {
    return <div className="centered focused">No favs found!</div>;
  }

  const firstIteration = Object.entries(loadedFavs[0]);
  const secondItem = Object.entries(firstIteration[0][1]);

  console.log(firstIteration, secondItem[0][1]);

  return (
    <>
      <FavouriteList favs={loadedFavs} />
    </>
  );
};
export default Favourite;
