import React, { useCallback, useEffect, useState } from "react";
const Home = () => {
  const [vehicles, setVehicles] = useState("");
  const fetchCar = useCallback(async () => {
    try {
      const response = await fetch(
        "https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForManufacturerAndYear/988?year=2014&format=json"
      );
      if (!response.ok) {
        throw new Error("sth wrong");
      }
      const data = await response.json();
      const transformedData = data.Results;
      setVehicles(transformedData);
    } catch (error) {
      console.log(error.message);
    }
  }, []);
  useEffect(() => {
    fetchCar();
  }, [fetchCar]);
  console.log(vehicles);

  let content = <p>No cars</p>;
  if (vehicles.length > 0) {
    content = vehicles.map((data) => {
      return (
        <>
          <div className="focused">
            <span>{data.MakeId}</span>
            <span>{data.MakeName}</span>
          </div>
        </>
      );
    });
  }

  return (
    <>
      <div className="centered focused">Home</div>
      <div>{content}</div>
    </>
  );
};

export default Home;
