import React, { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { fetchCarForDatabase } from "../lib/api";
import useHttp from "./../hooks/use-http";

import styles from "./Database.module.css";
import CarSearch from "./../components/Database/CarSearch";

const Database = () => {
  const [getCar, setGetCar] = useState("");
  const [getYear, setGetYear] = useState("");
  const [isCarSearch, setIsCarSearch] = useState(true);

  //   useEffect(() => {}, [getCarHandler]);

  const searchtypeHandler = (e) => {
    if (e.target.htmlFor === "car-search") {
      setIsCarSearch(true);
    }
    if (e.target.htmlFor === "factory-search") {
      setIsCarSearch(false);
    }
  };

  return (
    <>
      <div className={styles.checkbox}>
        <input type="radio" name="radio" id="car-search" defaultChecked />
        <label
          onClick={searchtypeHandler}
          htmlFor="car-search"
          className={styles["form-control"]}
        >
          Car Search
        </label>
        <input type="radio" name="radio" id="factory-search" />
        <label
          onClick={searchtypeHandler}
          htmlFor="factory-search"
          className={styles["form-control"]}
        >
          Factory Search
        </label>
      </div>

      {isCarSearch && <CarSearch />}
    </>
  );
};
export default Database;
