import React, { useEffect } from "react";

import styles from "./FactoryItem.module.css";
import { NavLink } from "react-router-dom";
import useHttp from "./../../hooks/use-http";
import { getDataInFavs } from "../../lib/api";

const FactoryItem = (props) => {
  const addToFavsHandler = (e) => {
    e.preventDefault();
    console.log(props.DOTCode);
  };
  const HandleOpenModal = () => {
    props.onOpenModal();
    const specificFactory = props;
    props.onViewFactory(specificFactory);
  };

  ///////////// get-keys ////////////////////
  const {
    sendRequest: getKeys,
    status: getStatus,
    error: getError,
    data: loadedFavs,
  } = useHttp(getDataInFavs, true);

  useEffect(() => {
    if (props.DOTCode) {
      getKeys();
      console.log("sent", getStatus);
    }
  }, [getKeys]);

  let getDOTCodes = [];
  if (getStatus === "pending") {
    return <div>pending</div>;
  }
  if (getError) {
    return <div className="centered focused">{getError}</div>;
  }
  if (getStatus === "completed" && loadedFavs) {
    getDOTCodes = Object.keys(loadedFavs);
  }

  if (getStatus === "completed" && !loadedFavs) {
    return <div className="centered focused">No favs found!</div>;
  }
  if (getDOTCodes.includes(props.DOTCode)) {
    console.log(getDOTCodes);
    // setIsAddedToFavs(true);
    // setAlreadyExistsInFavs(true);
  }
  ///////////// END-get-keys ////////////////////

  return (
    <div className={styles["factory-item"]}>
      <div className={styles["add-to-favs-btn"]}>
        <button onClick={addToFavsHandler}>Add to favs</button>
      </div>
      <div className={styles["factory-name"]}>{props.Name}</div>
      <div className={styles["factory-location"]}>
        <span className={styles["factory-country"]}>{props.Country} </span>
        <span className={styles["factory-city"]}>{props.City}</span>
      </div>
      <div className={styles["view-factory-btn"]}>
        <button onClick={HandleOpenModal}>View factory</button>
      </div>
    </div>
  );
};
export default FactoryItem;
