import React from "react";
import styles from "./FavouriteCarItem.module.css";

import useHttp from "../../hooks/use-http";
import { removeDataFromFavs } from "../../lib/api";

const FavouriteCarItem = (props) => {
  const { sendRequest, status, error } = useHttp(removeDataFromFavs);

  const removeFavHandler = () => {
    sendRequest({ category: "vehicle", id: props.id });
    setTimeout(() => {
      props.onRefreshFavsHandler();
    }, 750);
  };
  return (
    <>
      <div className="listItem">
        <div className={styles.factoryInfo}>
          <div>{props.Name}</div>
          <div>{props.Model}</div>
          <div>{props.id}</div>
        </div>
        <div>
          <button onClick={removeFavHandler}>Remove from Favs</button>
        </div>
      </div>
    </>
  );
};

export default FavouriteCarItem;
