const FIREBASE_DOMAIN = "https://portfolio-2-a6a7c-default-rtdb.firebaseio.com";

export async function fetchCarForHome() {
  const response = await fetch(
    "https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForManufacturerAndYear/988?year=2014&format=json"
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function fetchCarListId() {
  const response = await fetch(
    `https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForVehicleType/car?format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }
  return data;
}

export async function fetchCarForDatabase(carForDatabase) {
  const response = await fetch(
    // `https://vpic.nhtsa.dot.gov/api/vehicles/GetModelsForMakeIdYear/makeId/440/modelyear/2016?format=json`
    `https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformakeyear/make/${carForDatabase.manufacturer}/modelyear/${carForDatabase.year}?format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function fetchFactoryForDatabase(factoryForDatabase) {
  const response = await fetch(
    `https://vpic.nhtsa.dot.gov/api/vehicles/GetEquipmentPlantCodes?year=${factoryForDatabase.year}&equipmentType=${factoryForDatabase.equipmentType}&reportType=${factoryForDatabase.reportType}&format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function addToFavs(savedData) {
  const response = await fetch(`${FIREBASE_DOMAIN}/favs/${savedData.id}.json`, {
    method: "POST",
    body: JSON.stringify(savedData.savedData),
    headers: { "Content-type": "application/json" },
  });
  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || "Could not save data");
  }
  return null;
}

export async function getDataInFavs() {
  const response = await fetch(`${FIREBASE_DOMAIN}/favs.json`);
  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || "Could not fetch favs");
  }
  return data;
}

export async function removeDataFromFavs(idFav) {
  const response = await fetch(`${FIREBASE_DOMAIN}/favs/${idFav}.json`, {
    method: "DELETE",
    body: JSON.stringify(),
    headers: { "Content-type": "application/json" },
  });
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "Could not delete fav.");
  }
  return null;
}
