import React, { useEffect } from "react";
import { fetchCarForDatabase } from "../lib/api";
import useHttp from "./../hooks/use-http";

const Database = () => {
  const { sendRequest, status, data, error } = useHttp(fetchCarForDatabase);
  const getCarHandler = () => {
    sendRequest({ manufacturer: "volvo", year: 2014 });
  };
  useEffect(() => {
    getCarHandler();
  }, [getCarHandler]);
  console.log(status, data);

  return (
    <>
      {status}
      <button onClick={getCarHandler}>Get a car</button>
    </>
  );
};
export default Database;
