export async function fetchCarForHome(year = 2014) {
  const response = await fetch(
    `https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForManufacturerAndYear/988?year=${year}&format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function fetchCarForDatabase() {
  const response = await fetch(
    "https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForManufacturerAndYear/988?year=2014&format=json"
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}
