export async function fetchCarForHome() {
  const response = await fetch(
    "https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForManufacturerAndYear/988?year=2014&format=json"
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function fetchCarForDatabase(carForDatabase) {
  const response = await fetch(
    `https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformakeyear/make/${carForDatabase.manufacturer}/modelyear/${carForDatabase.year}?format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function fetchFactoryForDatabase(factoryForDatabase) {
  const response = await fetch(
    `https://vpic.nhtsa.dot.gov/api/vehicles/GetEquipmentPlantCodes?year=2015&equipmentType=16&reportType=closed&format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}
