import React, { useEffect } from "react";
import { removeDataFromFavs } from "../../lib/api";
import styles from "./FavouriteItem.module.css";
import useHttp from "../../hooks/use-http";

const FavouriteItem = (props) => {
  const { sendRequest, status, error } = useHttp(removeDataFromFavs);
  console.log(props);
  const removeFavHandler = () => {
    console.log(props.DOTCode);
    sendRequest(props.DOTCode);
    setTimeout(() => {
      props.onRefreshFavsHandler();
    }, 750);
  };

  return (
    <>
      <div className={styles.favInfo}>
        <div className={styles.factoryInfo}>
          <div>{props.Name}</div>
          <div>{props.Country}</div>
          <div>{props.City}</div>
          <div>{props.Address}</div>
          <div>{props.PostalCode}</div>
          <div>{props.DOTCode}</div>
          <div>{props.Status}</div>
        </div>
        <button onClick={removeFavHandler}>Remove from Favs</button>
      </div>
    </>
  );
};
export default FavouriteItem;
