export async function fetchCarForHome() {
  const response = await fetch(
    "https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForManufacturerAndYear/988?year=2014&format=json"
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}

export async function fetchCarForDatabase(manufacturer = nissan, year = 2012) {
  const response = await fetch(
    `https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformakeyear/make/${manufacturer}/modelyear/${year}?format=json`
  );
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || "sth wrong");
  }

  const fetchedData = data;
  return fetchedData;
}
